-- 导出  表 lala.t_article 结构
DROP TABLE IF EXISTS `t_article`;
CREATE TABLE IF NOT EXISTS `t_article` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `user_id` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '用户ID',
  `title` varchar(150) NOT NULL DEFAULT '0' COMMENT '文章标题',
  `cover` varchar(150) NOT NULL DEFAULT '0' COMMENT '封面图路径',
  `type` tinyint(1) NOT NULL DEFAULT '0' COMMENT '文章类型 0:图文 1:视频',
  `status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '文章状态: 0:待审核 1:发布 2:审核不通过 3:后台删除 4:用户删除',
  `category_id` int(11) NOT NULL DEFAULT '0' COMMENT '文章分类ID',
  `show_num` int(11) NOT NULL DEFAULT '0' COMMENT '阅读次数',
  `create_time` timestamp NOT NULL DEFAULT '2018-01-01 00:00:00' COMMENT '发布时间',
  `operate_time` timestamp NOT NULL DEFAULT '2018-01-01 00:00:00' COMMENT '审核通过时间',
  `update_time` timestamp NOT NULL DEFAULT '2018-01-01 00:00:00' COMMENT '修改时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=121 DEFAULT CHARSET=utf8 COMMENT='文章主表';

